const SendMessage=async()=>{let e=document.contactform.uname.value,t=document.contactform.email.value,a=document.contactform.message.value,o=!1;""!=e&&""!=t&&""!=a&&(o=!0);let l={name:e,email:t,message:a},s="https://localhost:5001/message",n=JSON.stringify(l);if(o){let e=await fetch(s,{method:"POST",headers:{"Content-Type":"application/json;charset=utf-8"},body:n});if(e.ok){let t=await e.text();alert(t)}else console.log("Connection error: "+e.status)}else alert("You need to fill out all the required fields first!")};