const LoadProjects=async()=>{let t="http://api.ignurof.xyz/projects",e=await fetch(t);if(e.ok){let t=await e.json();GenerateProjectCards(t.projects)}else console.log("Connection error: "+e.status)},GenerateProjectCards=t=>{let e=document.getElementById("cards"),n="";for(let e=0;e<t.length;e++){let o=`<div id="card">\n        <a href="/project.html?projectid=${t[e].id}">\n        <button type="submit">\n        <img src="img/${t[e].images[0]}" alt="Image Text"/>\n        <h4>${t[e].title}</h4>\n        <p>${t[e].summary}</p>\n        </button>\n        </a>\n        </div>`;n+=o}e.insertAdjacentHTML("afterbegin",n)};(document.readyState="complete")&&LoadProjects();