const LoadProjects=async()=>{let e="https://localhost:5001/projects",t=await fetch(e);if(t.ok){let e=await t.json();GenerateProjectCards(e)}else Debug("Connection error: "+t.status)},GenerateProjectCards=e=>{let t=document.getElementById("cards"),a="";for(let n=0;n<e.length;n++)a=`<div id="card">\n        <a href="/project.html?projectid=${e[n].id}">\n        <button type="submit">\n        <img src="${e[n].imageA}'" alt="Image Text"/>\n        <h4>${e[n].name}</h4>\n        <p>${e[n].summary}</p>\n        </button>\n        </a>\n        </div>`,t.insertAdjacentHTML("afterbegin",a)};(document.readyState="complete")&&LoadProjects();