const LoadProjects=async()=>{let e="http://api.ignurof.xyz/projects",t=await fetch(e);if(t.ok){let e=await t.json();GenerateProjectCards(e)}else console.log("Connection error: "+t.status)},GenerateProjectCards=e=>{let t=document.getElementById("cards"),n="";for(let t=0;t<e.length;t++){let a=`<div id="card">\n        <a href="/project.html?projectid=${e[t].id}">\n        <button type="submit">\n        <img src="${e[t].imageA}'" alt="Image Text"/>\n        <h4>${e[t].name}</h4>\n        <p>${e[t].summary}</p>\n        </button>\n        </a>\n        </div>`;n+=a}t.insertAdjacentHTML("afterbegin",n)};(document.readyState="complete")&&LoadProjects();